Bank5 = ({ setPhaseThree, setProgress }) => {
  const {
    register,
    handleSubmit,
    watch,
    reset,

    formState: { errors },
  } = useForm();
  const [file, setfile] = useState(null);
  const { user, userLogin } = useContext(AuthContext);
  const [fileuploaded, setFileuploaded] = useState(null);

  const headers = {
    "Content-Type": "application/json",
    Authorization: `Bearer ${user.token}`,
  };

  const onFileUpload = (e) => {
    setfile(e.target.files[0], "AI");
    console.log(file);
    handleUpload();
  };

  const handleUpload = async (data) => {
    if (file) {
      toast.loading("Uploading File");
      const data = new FormData();
      data.append("name", file.name);
      data.append("department", user.email);
      data.append("file", file);

      await axios
        .post(`${process.env.NEXT_PUBLIC_BUCKET}/file/upload`, data)
        .then(async (res) => {
          setFileuploaded(res.data);
          await axios
            .put(
              `${process.env.NEXT_PUBLIC_URL}/api/updateUserDetails`,
              {
                documentFile: res.data,
                type: user.type,
                email: user.email,
                profileReady: "Done",
              },
              { headers: headers }
            )
            .then((response) => {
              toast.dismiss();
              toast.success("Document  Uploaded Successfully");
              setProgress(80);
              setPhaseThree(true);
            })
            .catch((error) => {
              console.log(error);
              toast.dismiss();
              console.log(error); // Handle Login Error
              toast.error("Something Went Wrong");
            });
        });
    }
  };

  return (
    <div>
      <div className="flex flex-col p-2 w-[250px] md:w-auto">
        <form action="" onSubmit={handleSubmit(handleUpload)}>
          <p className="text-center text-sm text-gray-600 s">
