  } = useContext(AuthContext);

  const SendData = async () => {
    toast.dismiss();
    if (timeToSend.length == 3 && dayToSend.length == 3) {
      await axios
        .post(
          `${process.env.NEXT_PUBLIC_URL}/api/users/uploadDayTime`,
          {
            email: user.email,
            day: dayToSend,
            time: timeToSend,
          },
