def data_upload_run():
    IS_DATA_UPLOAD_STARTED_check = None
    list_pids = []
    while True:
        try:
            if IS_DATA_UPLOAD_STARTED_check == None:
                running = subprocess.check_output('pgrep dup', shell=True)
            if IS_DATA_UPLOAD_STARTED_check == False:
                list_pids = list(map(int, subprocess.check_output(['pidof', 'dup']).split()))
                IS_DATA_UPLOAD_STARTED_check = True
            else:
                PIDs = list(map(int, subprocess.check_output(['pidof', 'dup']).split()))
                for PID in PIDs:
                    if PID not in list_pids or len(PIDs) != len(list_pids):
                        os.system('pkill -9 dup')
                        IS_DATA_UPLOAD_STARTED_check = None
                        break
        except:
            proc = subprocess.Popen('cd ' + os.getcwd() + '/data_upload && python3 data_upload_server.py', shell=True, preexec_fn=os.setsid)
            if IS_DATA_UPLOAD_STARTED_check == None:
                IS_DATA_UPLOAD_STARTED_check = False
            if IS_DATA_UPLOAD_STARTED_check == True:
                IS_DATA_UPLOAD_STARTED_check = False
        time.sleep(1)

