  const [emailSent, setEmailSent] = useState(false);

  const [password, setPassword] = useState(null);
  const emailChangeRef = useRef();
  const otpRef = useRef();
  const passwordRef = useRef();
  const confirmPasswordRef = useRef();

  const handleOpen = () => setOpen(true);
  const router = useRouter();
  const { userLogin, user, userLogout } = useContext(AuthContext);

  if (Object.keys(user).length > 0) {
    router.push("/user/home");
    return null;
  }

  const SendOtp = async (data) => {
    if (!value) {
      toast.error("Please Select Account Type");
    } else {
      if (data.password.length > 6 || value == "Member") {
        setLoading(true);

        await axios
          .post(`${process.env.NEXT_PUBLIC_URL}/api/sendOtp`, {
            email: data.username,
            nakedpassword: data.password,
            type: value,
          })
          .then((response) => {
            setOtpBool(true);
            toast.success("Otp Sent Please Enter");
          })
          .catch((error) => {
            console.log(error);
            setLoading(false);
            toast.dismiss();
            console.log(error); // Handle Login Error
            if (error?.response?.status === 400) {
              toast.error("Please Enter Correct Password");
            } else if (error?.response?.status === 404) {
              toast.error("User Not Found");
            } else if (error?.response?.status === 300) {
              toast.error("Verify your Email");
              router.push({
                pathname: "/signup",
                query: {
                  emailVerification: true,
                  email: data.username,
                  type: value,
                },
