addTime = (item) => {
    if (timeToSend.length < 3 || timeToSend.includes(item)) {
      if (!timeToSend.includes(item)) {
        setTimeToSend([...timeToSend, item]);
      } else {
        let arr = [...timeToSend];
        let index = arr.findIndex((value) => {
          return value == item;
        });

        arr.splice(index, 1);
        setTimeToSend(arr);
        console.log(timeToSend);
      }
      console.log(timeToSend);
