import { DataTypes, Model } from 'sequelize';
import { config } from '../config/config';

interface ConversationAttributes {
    id: string;
    members: Array<any>;
    messages?: string;
    Setype: string;
    ReType: string;
    meetingTime?: string;
    meetingLink?: string;
    meetingDate?: string;
    finished?: boolean;
    startingLink?: string;
    Mtd?: string;
}

export class ConversationInstance extends Model<ConversationAttributes> {
    declare id: string;
    declare members: Array<any>;
    declare Setype: string;
    declare ReType: string;
    declare messages?: string;
    declare meetingTime: string;
    declare startingLink?: string;
    declare meetingLink: string;
    declare meetingDate?: string;
    declare finished?: boolean;
    declare Mtd?: string;
}

ConversationInstance.init(
    {
        id: {
            type: DataTypes.UUID,
            primaryKey: true,
            allowNull: false
        },
        members: {
            type: DataTypes.ARRAY(DataTypes.STRING),
            allowNull: false
        },
        ReType: {
            type: DataTypes.STRING,
            allowNull: false
        },
        Setype: {
            type: DataTypes.STRING,
            allowNull: false
        },
        messages: {
            type: DataTypes.STRING,
            allowNull: true
        },
        meetingTime: {
            type: DataTypes.STRING,
            allowNull: true
        },
        startingLink: {
            type: DataTypes.STRING,
            allowNull: true
        },
        meetingLink: {
            type: DataTypes.STRING,
            allowNull: true
        },
        meetingDate: {
            type: DataTypes.STRING,
            allowNull: true
        },
        Mtd: {
            type: DataTypes.STRING,
            allowNull: true
        },
        finished: {
            type: DataTypes.BOOLEAN,
            allowNull: true
        }
    },
    {
        sequelize: config.db,
        tableName: 'conversation'
    }
);

