    parser.add_argument('--app_starttime', type=int, default=0)
    parser.add_argument('--app_endtime', type=int, default=24)
    parser.add_argument('--app_interval', type=bool, default=True)
    parser.add_argument('--app_interval_time', type=int, default=2)
    args = parser.parse_args()
    flag = 1
    sleeping = False
    start_time = time.time()
    set_program_time_interval = False
    interval_time = 500
    if get_phase_one_app_Enable_system_restart():
        os.popen('shutdown -r 10:00')
    while True:
        FLAG_IS_common_MONITORING_STARTED = get_phaseone_app_monitoring_started()
        if FLAG_IS_common_MONITORING_STARTED == False:
            if flag == 1:
                proc = subprocess.Popen([args.APP], shell=True, preexec_fn=os.setsid)
                flag = 2
            if flag == 2:
                if args.app_starttime <= int(str(datetime.datetime.now()).split()[1].split(':')[0]) and int(str(datetime.datetime.now()).split()[1].split(':')[0]) <= args.app_endtime:
                    sleeping = False
                else:
                    os.killpg(os.getpgid(proc.pid), signal.SIGTERM)
                    sleeping = True
            if set_program_time_interval:
                if time.time() - start_time >= interval_time:
                    print('restartting')
                    os.killpg(os.getpgid(proc.pid), signal.SIGTERM)
