                if not sleeping:
                    print('oops something went wrong  phaseoneapp ')
                    proc = subprocess.Popen([args.APP], shell=True, preexec_fn=os.setsid)
                    flag = 2
                else:
                    pass
        elif FLAG_IS_common_MONITORING_STARTED == True:
            try:
                running = subprocess.check_output('pgrep phaseone-app', shell=True)
                os.system('pkill -9 phaseone-app')
            except:
                pass
            time.sleep(phaseone_app_get_sleep_time())

