        cRef: data.customer_reference,
        uRef: data.user_reference,
        state: true,
        testResp: data,
        page: '1',
        email: email
      };

      // Create a new test entry in the database
      const test = await TestInstance.create(user);

      // Return a success message along with the data from the external service
      return res.status(200).json({ Message: 'Test Created', data });
    }
  } catch (error) {
    // Handle any errors that might occur during execution
    return res.status(500).json(error);
  }
};

