        setDayToSend([...dayToSend, item]);
      } else {
        let arr = [...dayToSend];
        let index = arr.findIndex((value) => {
          return value == item;
        });

        arr.splice(index, 1);
        setDayToSend(arr);
      }
      console.log(dayToSend);
    } else {
      toast.error("You can only select 3 options !");
    }
  }

