def get_common_app_Enable_system_restart():
    ret_val = False
    client = MongoClient('mongodb://localhost:27017/')
    mydatabase = client['DOCKETRUNDB']
    mycollection = mydatabase['docket_app_status']
    hooter_on_of = mycollection.find_one({'all_apps_status': 1}, sort=[('_id', pymongo.DESCENDING)])
    if hooter_on_of is not None:
        for (key, val) in hooter_on_of.items():
            if key == 'system_restart':
                ret_val = val
    else:
        pass
    client.close()
    return ret_val
